<template>
  <div>
    <silk-button type="primary" @click="showCalendar">选择多个日期</silk-button>
    <silk-calendar
      v-model:visible="visible"
      type="multiple"
      @confirm="onConfirm"
      @cancel="onCancel"
    />
  </div>
</template>

<script setup>
import { ref } from 'vue';

const visible = ref(false);

const showCalendar = () => {
  visible.value = true;
};

const onConfirm = (_, dates) => {
  visible.value = false;
  console.log('选择了', dates.length, '个日期');
};

const onCancel = () => {
  visible.value = false;
};
</script>
