@Builder
func BasicImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 100.vp,
      height: 100.vp
    )
  )
}

@Builder
func FitImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 10.vp,
      height: 10.vp,
      fit: SilkImageFit.CONTAIN
    )
  )
}

@Builder
func PositionImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 10.vp,
      height: 10.vp,
      fit: SilkImageFit.COVER,
      position: SilkImagePosition.LEFT
    )
  )
}

@Builder
func RoundImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 10.vp,
      height: 10.vp,
      round: true
    )
  )
}

@Builder
func LoadingContent() {
  Column() {
    SilkLoading(size: 20.vp)
  }
  .width(100.percent)
  .height(100.percent)
  .justifyContent(FlexAlign.Center)
  .alignItems(HorizontalAlign.Center)
  .backgroundColor(Color(0xf7, 0xf8, 0xfa, alpha: 1.0))
}

@Builder
func LoadingImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 10.vp,
      height: 10.vp
    ),
    Loading: LoadingContent,
    hasLoading: true
  )
}

@Builder
func ErrorContent() {
  Column() {
    Text("加载失败")
      .fontSize(14.vp)
      .fontColor(Color(0x96, 0x97, 0x99, alpha: 1.0))
  }
  .width(100.percent)
  .height(100.percent)
  .justifyContent(FlexAlign.Center)
  .alignItems(HorizontalAlign.Center)
  .backgroundColor(Color(0xf7, 0xf8, 0xfa, alpha: 1.0))
}

@Builder
func ErrorImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 10.vp,
      height: 10.vp
    ),
    Error: ErrorContent,
    hasError: true
  )
}

@Builder
func EventImageContent() {
  SilkImage(
    src: "https://fastly.jsdelivr.net/npm/@vant/assets/cat.jpeg",
    props: SilkImageOptions(
      width: 10.vp,
      height: 10.vp
    ),
    click: { _ => PromptAction.showToast(message: "点击图片") },
    load: { => PromptAction.showToast(message: "图片加载完成") },
    error: { => PromptAction.showToast(message: "图片加载失败") }
  )
}
