SilkToast.loading(SilkToastOptions(message: "加载中...", loadingType: SilkLoadingType.SPINNER))

let toast = SilkToast.loading(
    SilkToastOptions(
        duration: 0,
        forbidClick: true,
        message: "倒计时 3 秒"
    )
);

spawn {
    =>
    var second = 3;
    while (second > 0) {
        sleep(1 * Duration.second);
        second--;
        if (second > 0) {
            toast.setMessage?("倒计时 ${second.toString()} 秒")
        } else {
            SilkToast.closeToast()
        }
    }
}

